import React, { Component } from 'react';
import TextField from '@material-ui/core/TextField';
import { Mutation } from "react-apollo";
import { useMutation } from '@apollo/react-hooks'
import {POSTS_QUERY} from "../../services/createBroker";
import CircularProgress from "@material-ui/core/CircularProgress";
import Button from "@material-ui/core/Button";


/*const addNewBroker = (brokerInput) => <Mutation mutation={POSTS_QUERY(brokerInput)}>
    {({loading, error, data}) => {
        if (loading) return <CircularProgress></CircularProgress>;
        if (error) return <p>Error...!</p>;

        return console.log(data);
    }}
</Mutation>;*/

class FormCreateBroker extends React.Component {

    state = {
        broker: {
            name: '',
            address: ''
        }
    };



    render() {
        return (
            <Mutation mutation={POSTS_QUERY}>
                { createBroker => (
                    <form onSubmit={ e => {
                        e.preventDefault();

                        const {name, address} = this.state.broker;
                        const input = {
                            name,
                            address
                        };
                        createBroker({
                            variables:{input}
                        })
                    }
                    }>
                        <input type='text' placeholder='Name' onChange={e => {
                            this.setState({
                                broker: {
                                    ...this.state.broker,
                                    name:e.target.value
                                }
                            })}
                        }/>
                        <input type='text' placeholder='Address' onChange={e => {
                            this.setState({
                                broker: {
                                    ...this.state.broker,
                                    address:e.target.value
                                }
                            })}
                        }/>
                        <button type="submit">Confirmar</button>
                    </form>
                )}
            </Mutation>
        );
    }
}

export default FormCreateBroker;

