const FormCreateBroker = () => {
    let input;
    const [createBroker, { data }] = useMutation(POSTS_QUERY);

    const [myValue, setValue] = useState('');

    const handleEmailChange = (e) => {
        this.setState({email: e.target.value});
    };
    const handlePasswordChange = (e) => {
        this.setState({password: e.target.value});
    };

    const handleLogin = () => {
        console.log("EMail: " + this.state.email);
        console.log("Password: " + this.state.password);
    };

/*    const brokerInput = {
        "brokerInput": {
            "name": "Barcelona F.C.",
            "address": "Barcelona F.C."
        }
    };*/

    return <form>
        <input type="text" name="email" placeholder="Email" value={this.state.email} onChange={handleEmailChange} />
        <input type="password" name="password" placeholder="Password" value={this.state.password} onChange={handlePasswordChange}/>
        <Button type="button" onClick={handleLogin}>Confirm</Button>
    </form>;
};

export default FormCreateBroker;


