import React, { Component } from 'react';
import TextField from '@material-ui/core/TextField';
import { Mutation } from "react-apollo";
import {POSTS_QUERY} from "../../services/createBroker";
import Button from "@material-ui/core/Button";
import './styles.css';


class FormCreateBroker extends Component {

    constructor(props){
        super(props);
        this.state = { showPopup: false };
    }

    togglePopup() {
        this.setState({
            showPopup: !this.state.showPopup
        });
    }

    state = {
        broker: {
            name: '',
            address: ''
        }
    };

    render() {
        return (
            <Mutation mutation={POSTS_QUERY}>
                { createBroker => (
                    <form noValidate onSubmit={ e => {
                        e.preventDefault();
                        const {name, address} = this.state.broker;
                        const brokerInput = {
                            name,
                            address
                        };
                        createBroker({
                            variables:{brokerInput}
                        })
                    }
                    }>
                        <div className="cont-inputs">
                            <TextField required type='text' placeholder='Name' onChange={e => {
                                this.setState({
                                    broker: {
                                        ...this.state.broker,
                                        name:e.target.value
                                    }
                                })}
                            }/>
                        </div>
                        <div className="cont-inputs">
                            <TextField required type='text' placeholder='Address' onChange={e => {
                                this.setState({
                                    broker: {
                                        ...this.state.broker,
                                        address:e.target.value
                                    }
                                })}
                            }/>
                        </div>
                        <div className="boton-confirm">
                            <Button type="submit">Confirmar</Button>
                        </div>
                    </form>
                )}
            </Mutation>
        );
    }
}

export default FormCreateBroker;
