import React from 'react';
import { gql } from 'apollo-boost';
import { mutation } from 'react-apollo';

export const POSTS_QUERY = gql`
    mutation CreateBroker($brokerInput: BrokerInput!) {
        createBroker(brokerInput: $brokerInput) {
            name
            address
        }
    }
`;
