import React from 'react';
import TextField from '@material-ui/core/TextField';
import {Query} from "react-apollo";
import {POSTS_QUERY} from "../../services/createBroker";
import CircularProgress from "@material-ui/core/CircularProgress";
import {Col, Row} from "react-flexbox-grid";
import Button from "@material-ui/core/Button";


export default FormCreateBroker = () => {
    return (
        <form noValidate autoComplete="off">
            <div>
                <TextField required id="standard-required" label="Required" defaultValue="Name of the Broker" />
                <TextField id="standard-required2" defaultValue="Address of the Broker" />
            </div>
            <Button>Confirm</Button>
        </form>
    );
}


const BrokerData = () => <Query query={POSTS_QUERY}>
    {({loading, error, data}) => {
        if (loading) return <CircularProgress></CircularProgress>;
        if (error) return <p>Error...!</p>;

        return data.brokers.map(broker => {
            return (
                <Row key={broker.id} className='cont-broker'>
                    <Col className='cont-col-descripcion'>
                        <Row>ID:</Row>
                        <Row>Name:</Row>
                        <Row>Address:</Row>
                    </Col>
                    <Col className='cont-col-valores'>
                        <Row>{broker.id}</Row>
                        <Row>{broker.name}</Row>
                        <Row>{broker.address}</Row>
                    </Col>
                </Row>
            )
        })
    }}
</Query>;
