import React, { Component } from 'react';
import TextField from '@material-ui/core/TextField';
import { Mutation } from "react-apollo";
import { POSTS_QUERY } from "../../services/createProperties";
import Button from "@material-ui/core/Button";
import './styles.css';

class FormCreateProperty extends Component {

    constructor(props){
        super(props);
    };

    state = {
        property: {
            broker: {
                id: ''
            },
            address: '',
            latitude: '',
            longitude: '',
            price: '',
            currency: ''
        }
    };

    render() {
        return (
            <Mutation mutation={POSTS_QUERY}>
                { createProperty => (
                    <form noValidate onSubmit={ e => {
                        e.preventDefault();

                        const {brokerId, address, latitude, longitude, price, currency} = this.state.property;
                        const propertyInput = {
                            brokerId: Number(brokerId),
                            address,
                            latitude: Number(latitude),
                            longitude: Number(longitude),
                            price: Number(price),
                            currency
                        };
                        createProperty({
                            variables:{propertyInput}
                        })
                    }
                    }>


                        <div className="cont-inputs-prop">
                            <TextField required type="number" placeholder='Broker ID' onChange={e => {
                                this.setState({
                                    property: {
                                        ...this.state.property,
                                        brokerId:e.target.value
                                    }
                                })}
                            }/>
                        </div>
                        <div className="cont-inputs-prop">
                            <TextField required type='text' placeholder='Address' onChange={e => {
                                this.setState({
                                    property: {
                                        ...this.state.property,
                                        address:e.target.value
                                    }
                                })}
                            }/>
                        </div>
                        <div className="cont-inputs-prop">
                            <TextField required type="number" step="any" placeholder='Latitude' onChange={e => {
                                this.setState({
                                    property: {
                                        ...this.state.property,
                                        latitude:e.target.value
                                    }
                                })}
                            }/>
                        </div>
                        <div className="cont-inputs-prop">
                            <TextField required type="number" step="any" placeholder='Longitude' onChange={e => {
                                this.setState({
                                    property: {
                                        ...this.state.property,
                                        longitude:e.target.value
                                    }
                                })}
                            }/>
                        </div>
                        <div className="cont-inputs-prop">
                            <TextField required type="number" placeholder='Price' onChange={e => {
                                this.setState({
                                    property: {
                                        ...this.state.property,
                                        price:e.target.value
                                    }
                                })}
                            }/>
                        </div>
                        <div className="cont-inputs-prop">
                            <TextField required type='text' placeholder='Currency' onChange={e => {
                                this.setState({
                                    property: {
                                        ...this.state.property,
                                        currency:e.target.value
                                    }
                                })}
                            }/>
                        </div>
                        <div className="boton-confirm">
                            <Button type="submit">Confirm</Button>
                        </div>
                    </form>
                )}
            </Mutation>
        );
    }
}

export default FormCreateProperty;
