import React from 'react';
import CircularProgress from '@material-ui/core/CircularProgress';
import Paper from '@material-ui/core/Paper';
import AppBar from "@material-ui/core/AppBar";
import Typography from '@material-ui/core/Typography';
import Toolbar from '@material-ui/core/Toolbar';
import { Grid, Col, Row } from 'react-flexbox-grid';
import { Query } from 'react-apollo';
import { gql } from 'apollo-boost';
import './App.css';

const POSTS_QUERY = gql`
    {
        brokers {
            id
            name
            address
        }
    }
`;


function App() {
  return (
    <Grid>
        <Row>
            <AppBar position='sticky'>
                <Toolbar>
                    <Typography variant='title' color='orange'>
                        Admin Panel Brokers
                    </Typography>
                </Toolbar>
            </AppBar>
        </Row>
        <Row>
            <Col xs={12} md={6}>
                <Query query={POSTS_QUERY}>
                    {({loading, error, data}) => {
                        if (loading) return <CircularProgress></CircularProgress>;
                        if (error) return <p>Error...!</p>;

                        return data.brokers.map(broker => {
                            return <p key={broker.id}>{broker.name} -- {broker.id}</p>
                        })
                    }}
                </Query>
            </Col>
            <Col xs={12} md={6}>
                <div className="Properties-details"></div>
            </Col>
        </Row>
    </Grid>
  );
}

export default App;
