const FormCreateBroker = () => {
    const [myValue, setValue] = useState('');

    const brokerInput = {
        "brokerInput": {
            "name": "Barcelona F.C.",
            "address": "Barcelona F.C."
        }
    };

    return <form noValidate autoComplete="off">
        <div>
            <TextField required id="broker-name" label="Required" placeholder="Name of the Broker" value={myValue}
                       onChange={(e) => setValue(e.target.value)}/>
            <TextField id="broker-address" placeholder="Address of the Broker"/>
        </div>
        <Button >Confirm</Button>
    </form>;
};

export default FormCreateBroker;
