import React from 'react';
import TextField from '@material-ui/core/TextField';
import {Mutation} from "react-apollo";
import {POSTS_QUERY} from "../../services/createBroker";
import CircularProgress from "@material-ui/core/CircularProgress";
import Button from "@material-ui/core/Button";

const addNewBroker = ({ }) => <Mutation mutation={POSTS_QUERY}>
    {({loading, error, data}) => {
        if (loading) return <CircularProgress></CircularProgress>;
        if (error) return <p>Error...!</p>;

        return console.log(data);
    }}
</Mutation>;


class FormCreateBroker extends React.Component {
    constructor(props) {
        super(props);
    };

    save() {
        const { value } = this.state;

        // ... do logic
    };

    handleChange = (value) => {
        this.setState({ value });
    };

    render() {
        return (
            <form>
                <TextField id="some-unique-id" label="Some label" placeholder="Type text here..." value={this.state} onChange={this.handleChange}/>
            </form>
        );
    }
}

export default FormCreateBroker;

