import React, { Component } from 'react';
import TextField from '@material-ui/core/TextField';
import { Mutation } from "react-apollo";
import {POSTS_QUERY} from "../../services/createProperties";
import Button from "@material-ui/core/Button";

class FormCreateProperty extends Component {

    constructor(props){
        super(props);
    };

    state = {
        property: {
            address: '',
            latitude: '',
            longitude: '',
            price: '',
            currency: ''
        }
    };

    render() {
        return (
            <Mutation mutation={POSTS_QUERY}>
                { createProperty => (
                    <form noValidate onSubmit={ e => {
                        e.preventDefault();

                        const { address, latitude, longitude, price, currency} = this.state.property;
                        const propertyInput = {
                            address,
                            latitude: Number(latitude),
                            longitude: Number(longitude),
                            price: Number(price),
                            currency
                        };
                        createProperty({
                            variables:{propertyInput}
                        })
                    }
                    }>

                        <TextField required type='text' placeholder='Address' onChange={e => {
                            this.setState({
                                property: {
                                    ...this.state.property,
                                    address:e.target.value
                                }
                            })}
                        }/>
                        <TextField required type='text' placeholder='Latitude' onChange={e => {
                            this.setState({
                                property: {
                                    ...this.state.property,
                                    latitude:e.target.value
                                }
                            })}
                        }/>
                        <TextField required type='text' placeholder='Longitude' onChange={e => {
                            this.setState({
                                property: {
                                    ...this.state.property,
                                    longitude:e.target.value
                                }
                            })}
                        }/>
                        <TextField required type='text' placeholder='Price' onChange={e => {
                            this.setState({
                                property: {
                                    ...this.state.property,
                                    price:e.target.value
                                }
                            })}
                        }/>
                        <TextField required type='text' placeholder='Currency' onChange={e => {
                            this.setState({
                                property: {
                                    ...this.state.property,
                                    currency:e.target.value
                                }
                            })}
                        }/>
                        <Button type="submit">Confirmar</Button>
                    </form>
                )}
            </Mutation>
        );
    }
}

export default FormCreateProperty;
