import React from 'react';
import TextField from '@material-ui/core/TextField';
import {Mutation} from "react-apollo";
import {POSTS_QUERY} from "../../services/createBroker";
import CircularProgress from "@material-ui/core/CircularProgress";
import {Col, Row} from "react-flexbox-grid";
import Button from "@material-ui/core/Button";

const addNewBroker = ({ }) => <Mutation mutation={POSTS_QUERY}>
    {({loading, error, data}) => {
        if (loading) return <CircularProgress></CircularProgress>;
        if (error) return <p>Error...!</p>;

        return console.log(data);
    }}
</Mutation>;

const FormCreateBroker = () => {
    const state = {
        myValue: '',
    };

    const save = () => {
        const { value } = this.state;
    };

    const handleChange = (value) => {
        this.setState({ value });
    };

    return (
        <form noValidate autoComplete="off" {...props} >
            <div>
                <TextField required id="broker-name" label="Required" placeholder="Name of the Broker" value={this.state.value} onChange={handleChange}/>
                <TextField id="broker-address" placeholder="Address of the Broker" />
            </div>
            <Button onClick={addNewBroker}>Confirm</Button>
        </form>
    );
};

export default FormCreateBroker;
