import React, { Component } from 'react';
import BrokerData from "../components/Brokers/BrokerData";
import {Grid} from "@material-ui/core";
import Button from '@material-ui/core/Button';
import FormDialog from "../components/Popup";
import './styles.css';
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';


function rand() {
    return Math.round(Math.random() * 20) - 10;
}

function getModalStyle() {
    const top = 50 + rand();
    const left = 50 + rand();

    return {
        top: `${top}%`,
        left: `${left}%`,
        transform: `translate(-${top}%, -${left}%)`,
    };
}

const classes = useStyles();
// getModalStyle is not a pure function, we roll the style only on the first render
const [modalStyle] = React.useState(getModalStyle);
const [open, setOpen] = React.useState(false);

const handleOpen = () => {
    setOpen(true);
};

class BrokersContainer extends Component {
    render() {
        return (
            <Grid>
                <h4>Brokers:</h4>
                <Button aria-controls="simple-menu" aria-haspopup="true" onClick={handleOpen}>
                    Create Broker
                </Button>
                <div className='cont-broker-data'>
                    <BrokerData></BrokerData>
                </div>
            </Grid>
        )
    }
}

export default BrokersContainer;
